mapper.insert:
INSERT INTO BADGE_COUNT (
    MEMBER_ID,
    BADGE_TYPE,
    COUNT
) VALUES (
    #{memberId},
    #{badgeType},
    #{count}
)

mapper.update:
UPDATE BADGE_COUNT SET
     `COUNT` = `COUNT` + #{count}
WHERE 1=1
AND MEMBER_ID = #{memberId}
AND BADGE_TYPE = #{badgeType}
AND COUNT_ID = #{countId}
